import React from 'react';
import './App.css';

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      api: ""
    };
    this.callApi = this.callApi.bind(this);
  }
  callApi() {
    fetch(
      "https://localhost/phprest/paltimapi.php",{
        method: 'POST',
	      body: JSON.stringify({
          type: "all"
        }),
	      headers: {
          'Content-type': 'application/json; charset=UTF-8'
	      }}) 
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }))
  }
  componentDidMount() {
    this.callApi();
  }
  render () {
    console.log(this.state.api)
    if(this.state.api != ""){
  return (
    <div className="container">
      <table className="table table-dark table-bordered" id="table">
            <thead>
              <tr>
                <th scope="col">Building</th>
                <th scope="col">AppCode</th>
                <th scope="col">Rooms</th>
                <th scope="col">Level</th>
                <th scope="col">Surface</th>
                <th scope="col">Orienation</th>
                <th scope="col">Terrace</th>
              </tr>
            </thead>
            <tbody>
              {this.state.api.map((item) => {
                  return (
                    <tr key={item.Id} id={item.BUILDING}>
                      <td key={1}>{item.APPCODE}</td>
                      <td key={2}>{item.ROOMS}</td>
                      <td key={3}>{item.LEVEL}</td>
                      <td key={5}>{this.age(item.BirthDate)}</td>
                      <td key={6}>{this.entryDate(item.EntryDate)}</td>
                      <td>
                        <button 
                          className="noselect"
                          style={{textAlign: "center", width:"100%", marginTop: "2px"}}
                          key={item.UserName}
                          onClick={this.edit}
                          value={item.Id}
                          id={item.UserName}
                        >
                          Edit
                        </button>
                        <button 
                          className="noselect"
                          style={{textAlign: "center", width:"100%", marginTop: "2px"}}
                          key={item.UserName}
                          onClick={this.hide}
                          value={item.Id}
                        >
                          Delete
                        </button>
                       </td>
                    </tr>
                  );
              })}
            </tbody>
          </table>
    </div>
  );
            } else {
              return (
                <div>nu</div>
              )
            }
}








}



export default Table;
