import React from "react";
import "./App.css";
import  BootstrapTable  from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import {Modal, Button} from 'react-bootstrap';
import { Range } from 'rc-slider';
import 'rc-slider/assets/index.css';

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      api: "",
      room1: false,
      room2: false,
      room3: false,
      room4: false,
      building1: false,
      building2: false,
      floor0: false,
      floor1: false,
      floor2: false,
      floor3: false,
      floor4: false,
      floor5: false,
      floor6: false,
      bathroom1: false,
      bathroom2: false,
      forsale: false,
      sold: false,
      currentPage: 1,
      postsPerPage: 5,
      modalinfo:[],
      showmodal:false,
      show:false,
      sliderValues:[0,100]
    };

    this.callApi = this.callApi.bind(this);

  }
  //Call API
  callApi() {
    fetch(
      "https://localhost/phprest/paltimapi.php",
      {
        method: "POST",
        body: JSON.stringify({
          type: "all",
          room1: "da",
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8",
        },
      }
    )
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  componentDidMount() {
    this.callApi();
  }
  handleCheckboxChange(e) {
    this.setState(
      { [e.target.name]: e.target.checked},
      () => {
        this.sendRequest();
      }
    );
  }
  handleSlider(sliderValues){
    this.setState(
      {sliderValues}, 
      () => {
        this.sendRequest();
    })
  }
  async sendRequest() {
    await fetch(
      "https://localhost/phprest/paltimapi.php",
      {
        method: "POST",
        body: JSON.stringify({
          type: "sort",
          room1: this.state.room1,
          room2: this.state.room2,
          room3: this.state.room3,
          room4: this.state.room4,
          building1: this.state.building1,
          building2: this.state.building2,
          floor0: this.state.floor0,
          floor1: this.state.floor1,
          floor2: this.state.floor2,
          floor3: this.state.floor3,
          floor4: this.state.floor4,
          floor5: this.state.floor5,
          floor6: this.state.floor6,
          bathroom1: this.state.bathroom1,
          bathroom2: this.state.bathroom2,
          surface1: this.state.sliderValues[0],
          surface2: this.state.sliderValues[1],
          forsale: this.state.forsale,
          sold: this.state.sold,
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8",
        },
      }
    )
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  paginate(pageNumber) {
    this.setState({ currentPage: pageNumber });
  }

  render() {
    if (this.state.api !== "") {
    console.log(this.state)
      const handleClose = () => {
        this.setState({show: false});
      }
      const handleShow = () => { 
        this.setState({show: true});
      }
      const rowEvents = {
        onClick: (e, row, rowIndex) => {
          if(row.STATUS === "FOR SALE"){
          this.setState({modalinfo: row});
          toggleTrueFalse()
          }
        }
      };
      const toggleTrueFalse = () => {
        handleShow();
      }

      const ModalContent = () =>{
        return (
          <Modal show={this.state.show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>{this.state.modalinfo.APPCODE}</Modal.Title>

            </Modal.Header>
            <Modal.Body>
            <div className="container">
              <div className="row">
                <div className="col">
              <img src="https://picsum.photos/200/300"></img>

              </div>
              <div className="col">
                <hr></hr>
              <h6 className="modalth6">Surface : {this.state.modalinfo.SURFACE}</h6>
              <h6>Building : {this.state.modalinfo.BUILDING}</h6>
              <h6>Floor : {this.state.modalinfo.LEVEL}</h6>
              <h6>Number of rooms : {this.state.modalinfo.ROOMS}</h6>
              <h6>Bathrooms : {this.state.modalinfo.BATHROOMS}</h6>
              <h6 className="modalbh6">Terrace : {this.state.modalinfo.TERRACE}</h6>
              <hr></hr>
              </div>
              </div>
            </div>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>Close</Button>
            </Modal.Footer>
          </Modal>
        )
      }
      const columns = [
        {dataField: "BUILDING", text:"BUILDING"},
        {dataField: "APPCODE", text:"APPCODE"},
        {dataField: "ROOMS", text:"ROOMS"},
        {dataField: "BATHROOMS", text:"BATHROOMS"},
        {dataField: "LEVEL", text:"FLOOR"},
        {dataField: "SURFACE", text:"SURFACE"},
        {dataField: "TERRACE", text:"TERRACE"},
        {dataField: "ORIENTATION", text:"ORIENTATION"},
        {dataField: "STATUS", text:"STATUS"}
      ];
      let bathrooms = [null,0,0];
      let bathroomsnumbers = ["1","2"];
      let forsale = 0;
      let floors = [0,0,0,0,0,0,0]
      let floornumbers = ["0","1","2","3","4","5","6"]
      let buildings = [0,0]
      let rooms = [null,0,0,0,0];
      let roomnumbers = ["1","2","3","4"]
      for (let i = 0; i < this.state.api.length; ++i) {
        if (roomnumbers.includes(this.state.api[i].ROOMS) && this.state.api[i].STATUS === "FOR SALE") {
          ++rooms[this.state.api[i].ROOMS - 0];
        }
        if (this.state.api[i].BUILDING === "C1"  && this.state.api[i].STATUS === "FOR SALE") {
          ++buildings[0];
        }
        if (this.state.api[i].BUILDING === "C2"  && this.state.api[i].STATUS === "FOR SALE") {
          ++buildings[1];
        }
        if (floornumbers.includes(this.state.api[i].LEVEL)  && this.state.api[i].STATUS === "FOR SALE") {
          ++floors[this.state.api[i].LEVEL - 0];
        }
        if (bathroomsnumbers.includes(this.state.api[i].BATHROOMS)  && this.state.api[i].STATUS === "FOR SALE") {
          ++bathrooms[this.state.api[i].BATHROOMS - 0];
        }
        if (this.state.api[i].STATUS === "FOR SALE") {
          ++forsale;
        }
      }
      return (
        <div className="container-xxl">
          <div className="row">
            <div className="col">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Filters</h5>
                  <h6>Number of rooms</h6>
                  {roomnumbers.map((room) => {
                    return (
                      <div key={room}>
                          <input
                          type="checkbox"
                          name={"room" + room}
                          onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> {room} Room ---- Available : {rooms[room]}</label>
                      </div>
                    )
                  })}
                  <hr></hr>
                  <h6>Building</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="building1"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Building C1 ---- Available : {buildings[0]}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="building2"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Building C2 ---- Available : {buildings[1]}</label>
                  </div>
                  <hr></hr>
                  <div className="container">
                    <h6 style={{ marginLeft: "-15px" }}>Floor Level</h6>
                    <div className="row" style={{ marginLeft: "-15px" }}>
                      {floornumbers.map((floor) => {
                        return (
                          <div>
                            <input
                            type="checkbox"
                            name={"floor" + floor}
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> {floor} Floor ---- Available : {floors[floor]}</label>
                          </div>
                        )
                      })}

                    </div>
                  </div>
                  <hr></hr>
                  <h6>Bathrooms</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="bathroom1"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 1 Bathroom ---- Available : {bathrooms[1]}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="bathroom2"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 2 Bathroom ---- Available : {bathrooms[2]}</label>
                  </div>
                  <hr></hr>
                  <p>{this.state.sliderValues[0]}-{this.state.sliderValues[1]}</p>
                  <Range
                  min={0}
                  max={100}
                  onChange={this.handleSlider.bind(this)}
                  defaultValue={this.state.sliderValues}></Range>
                  <hr></hr>
                  <h6>Status</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="forsale"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> For Sale ---- Available : {forsale}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="sold"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Sold</label>
                  </div>
                </div>

              </div>


            </div>
            <div className="col">
            <BootstrapTable className="da"
            keyField="ID"
            data={this.state.api}
            columns={columns}
            pagination={paginationFactory()}
            rowEvents={rowEvents}
            />
            {this.state.show ? <ModalContent/> : null}

            </div>
          </div>



        </div>
      );
    } else {
      return <div>Api not started</div>;
    }
  }
}

export default Table;
