import React, { Component } from 'react'
import { withRouter } from "react-router-dom";

 class Pages extends Component {
    constructor(props) {
        super(props);
        this.state = {
            url: "https://localhost/phprest/paltimapi.php",
            image: "../images/de.jpg",
            appcode:"",
            api: "",
            src: "https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/appartmentPlans/"
        }
        this.callApi = this.callApi.bind(this);
    }
    componentDidMount() {
        this.callApi()
    }
    callApi() {
        let id = this.props.match.params.id;
        id = id.slice(1,id.length)
        this.setState({appcode: id.slice(0,id.length)});
        fetch(this.state.url, {
            method: "POST",
            body: JSON.stringify({
              type: "id",
              id: id
            }),
            headers: {
              "Content-type": "application/json; charset=UTF-8",
            },
          })
            .then((res) => res.json())
            .then((res) => this.setState({ api: res }));
      }
    render() {
        if (this.state.api !== "") {
        let src =this.state.src + this.state.appcode + ".png";
        return (
            <div>
                <div className="container">
                <div className="row">
                  <div className="col">
                  <img  src={src} width="900px" style={{marginLeft: "-150px"}}/>
                  </div>
                  <div className="col">
                    
                    <h6 className="modalth6" style={{marginTop: "200px"}}>
                        <h2>{this.state.appcode}</h2>
                        <hr></hr>
                      Surface : {this.state.api[0].SURFACE}
                    </h6>
                    <h6>Building : {this.state.api[0].BUILDING}</h6>
                    <h6>Floor : {this.state.api[0].LEVEL}</h6>
                    <h6>Number of rooms : {this.state.api[0].ROOMS}</h6>
                    <h6>Bathrooms : {this.state.api[0].BATHROOMS}</h6>
                    <h6 className="modalbh6">
                      Terrace : {this.state.api[0].TERRACE}
                    </h6>
                    <hr></hr>
                  </div>
                </div>
              </div>
                
            </div>
        )
    } else {
        return (
            <div></div>
        )
    }
}
}
export default withRouter(Pages)
