import React from "react";
import "./App.css";
import 'reactjs-popup/dist/index.css';
import Contact from "./Components/Contact";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Navigation from "./Components/Navigation";

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      api: "",
      room1: false,
      room2: false,
      room3: false,
      room4: false,
      building1: false,
      building2: false,
      gfloor: false,
      floor1: false,
      floor2: false,
      floor3: false,
      floor4: false,
      floor5: false,
      floor6: false,
      bathroom1: false,
      bathroom2: false,
      surface: 0,
      forsale: false,
      sold: false,
      currentPage: 1,
      postsPerPage: 5,
    };

    this.callApi = this.callApi.bind(this);
  }

  //Call API
  callApi() {
    fetch(
      "https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/paltimapi.php",
      {
        method: "POST",
        body: JSON.stringify({
          type: "all",
          room1: "da",
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8",
        },
      }
    )
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  componentDidMount() {
    this.callApi();
  }
  handleCheckboxChange(e) {
    this.setState(
      { [e.target.name]: e.target.value, surface: e.target.value },
      () => {
        this.sendRequest();
      }
    );
  }
  async sendRequest() {
    await fetch(
      "https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/paltimapiSort.php",
      {
        method: "POST",
        body: JSON.stringify({
          type: "sort",
          room1: this.state.room1,
          room2: this.state.room2,
          room3: this.state.room3,
          room4: this.state.room4,
          building1: this.state.building1,
          building2: this.state.building2,
          gfloor: this.state.gfloor,
          floor1: this.state.floor1,
          floor2: this.state.floor2,
          floor3: this.state.floor3,
          floor4: this.state.floor4,
          floor5: this.state.floor5,
          floor6: this.state.floor6,
          bathroom1: this.state.bathroom1,
          bathroom2: this.state.bathroom2,
          surface: this.state.surface,
          forsale: this.state.forsale,
          sold: this.state.sold,
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8",
        },
      }
    )
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  paginate(pageNumber) {
    this.setState({ currentPage: pageNumber });
  }
  render() {
    if (this.state.api !== "") {
      console.log(this.state.api);
      let room1 = 0,
        room2 = 0,
        room3 = 0,
        room4 = 0,
        buildingc1 = 0,
        buildingc2 = 0,
        groundfloor = 0,
        floor1 = 0,
        floor2 = 0,
        floor3 = 0,
        floor4 = 0,
        floor5 = 0,
        floor6 = 0,
        bathroom1 = 0,
        bathroom2 = 0,
        forsale = 0;
      let pageNumbers = [];
      const indexOfLastPost = this.state.currentPage * this.state.postsPerPage;
      const indexOfFirstPost = indexOfLastPost - this.state.postsPerPage;
      const currentPost = this.state.api.slice(
        indexOfFirstPost,
        indexOfLastPost
      );
      for (
        let i = 1;
        i <= Math.ceil(this.state.api.length / this.state.postsPerPage);
        i++
      ) {
        pageNumbers.push(i);
      }
      for (let i = 0; i < this.state.api.length; ++i) {
        if (this.state.api[i].ROOMS === "1") {
          ++room1;
        }
        if (this.state.api[i].ROOMS === "2") {
          ++room2;
        }
        if (this.state.api[i].ROOMS === "3") {
          ++room3;
        }
        if (this.state.api[i].ROOMS === "4") {
          ++room4;
        }
        if (this.state.api[i].BUILDING === "C1") {
          ++buildingc1;
        }
        if (this.state.api[i].BUILDING === "C2") {
          ++buildingc2;
        }
        if (this.state.api[i].LEVEL === "0") {
          ++groundfloor;
        }
        if (this.state.api[i].LEVEL === "1") {
          ++floor1;
        }
        if (this.state.api[i].LEVEL === "2") {
          ++floor2;
        }
        if (this.state.api[i].LEVEL === "3") {
          ++floor3;
        }
        if (this.state.api[i].LEVEL === "4") {
          ++floor4;
        }
        if (this.state.api[i].LEVEL === "5") {
          ++floor5;
        }
        if (this.state.api[i].LEVEL === "6") {
          ++floor6;
        }
        if (this.state.api[i].BATHROOMS === "1") {
          ++bathroom1;
        }
        if (this.state.api[i].BATHROOMS === "2") {
          ++bathroom2;
        }
        if (this.state.api[i].STATUS === "FOR SALE") {
          ++forsale;
        }
      }
      return (
        <div className="container-xl">
          <Router>
            <Navigation/>
          <Switch>
            <Route path="/contact" exact component={() => <Contact />} />
          </Switch>
          </Router>
          <Contact/>
          <div className="row">
            <div className="col">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Filters</h5>
                  <h6>Number of rooms</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="room1"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 1 Room ---- Available : {room1}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="room2"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 2 Rooms ---- Available : {room2}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="room3"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 3 Rooms ---- Available : {room3}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="room4"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 4 Rooms ---- Available : {room4}</label>
                  </div>
                  <hr></hr>
                  <h6>Building</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="building1"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Building C1 ---- Available : {buildingc1}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="building2"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Building C2 ---- Available : {buildingc2}</label>
                  </div>
                  <hr></hr>
                  <div className="container">
                    <h6 style={{ marginLeft: "-15px" }}>Floor Level</h6>
                    <div className="row" style={{ marginLeft: "-30px" }}>
                      <div className="col">
                        <div>
                          <input
                            type="checkbox"
                            name="gfloor"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> G Floor ---- Available : {groundfloor}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor1"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 1 ---- Available : {floor1}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor2"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 2 ---- Available : {floor2}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor3"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 3 ---- Available : {floor3}</label>
                        </div>
                      </div>
                      <div className="col">
                        <div>
                          <input
                            type="checkbox"
                            name="floor4"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 4 ---- Available : {floor4}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor5"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 5 ---- Available : {floor5}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor6"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 6 ---- Available : {floor6}</label>
                        </div>
                      </div>
                    </div>
                  </div>
                  <hr></hr>
                  <h6>Bathrooms</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="bathroom1"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 1 Bathroom ---- Available : {bathroom1}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="bathroom2"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 2 Bathroom ---- Available : {bathroom2}</label>
                  </div>
                  <hr></hr>
                  <div className="slidecontainer">
                    <h6> Surface : {this.state.surface}</h6>
                    <input
                      type="range"
                      min="1"
                      max="100"
                      className="multi-range"
                      name="surface"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <div></div>
                  </div>
                  <hr></hr>
                  <h6>Status</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="forsale"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> For Sale ---- Available : {forsale}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="sold"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Sold</label>
                  </div>
                </div>
              </div>
            </div>
            <div className="col">
              <table className="table-sm table-hover" id="table">
                <thead className="table">
                  <tr>
                    <th scope="col">Building</th>
                    <th scope="col">AppCode</th>
                    <th scope="col">Rooms</th>
                    <th scope="col">Bathrooms</th>
                    <th scope="col">Floor</th>
                    <th scope="col">Surface</th>
                    <th scope="col">Terrace</th>
                    <th scope="col">Orientation</th>
                    <th scope="col">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {currentPost.map((item) => {
                    return (
                      <tr key={item.ID}>
                        <td key={0} data-label="Building">
                          {item.BUILDING}
                        </td>
                        <td key={1} data-label="AppCode">
                          {item.APPCODE}
                        </td>
                        <td key={2} data-label="Rooms">
                          {item.ROOMS}
                        </td>
                        <td key={3} data-label="Bathrooms">
                          {item.BATHROOMS}
                        </td>
                        <td key={4} data-label="Level">
                          {item.LEVEL}
                        </td>
                        <td key={5} data-label="Surface">
                          {item.SURFACE}
                        </td>
                        <td key={6} data-label="Terrace">
                          {item.TERRACE}
                        </td>
                        <td key={7} data-label="Orientation">
                          {item.ORIENTATION}
                        </td>
                        <td key={8} data-label="Status">
                          {item.STATUS}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
              <nav aria-label="Page navigation example">
                <ul className="pagination">
                  {pageNumbers.map((number) => {
                    return (
                      <li key={number} className="page-item">
                        <a
                          onClick={() => this.paginate(number)}
                          href="#"
                          className="page-link"
                        >
                          {number}
                        </a>
                      </li>
                    );
                  })}
                </ul>
              </nav>
            </div>
          </div>
        </div>
      );
    } else {
      return <div>nu</div>;
    }
  }
}

export default Table;
