import React from 'react';
import './App.css';

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      api: ""
    };
    this.callApi = this.callApi.bind(this);
  }
  callApi() {
    fetch(
      "https://localhost/phprest/paltimapi.php",{
        method: 'POST',
	      body: JSON.stringify({
          type: "all"
        }),
	      headers: {
          'Content-type': 'application/json; charset=UTF-8'
	      }})
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  componentDidMount() {
    this.callApi();
  }
  render () {
  return (
    <div className="container">
      <table className="table table-dark table-bordered" id="table">
            <thead>
              <tr>
                <th scope="col">Picture</th>
                <th scope="col" className="selects" id="select1" onClick={this.handleSelect}>Username</th>
                <th scope="col">Phone</th>
                <th scope="col">Email</th>
                <th scope="col" className="selects" id="select2" onClick={this.handleSelect1}>Age</th>
                <th scope="col" className="selects" id="select3" onClick={this.handleSelect2}>Entry Date</th>
                <th scope="col">Edit/Delete</th>
              </tr>
            </thead>
            <tbody>
              {currentPost.map((item) => {
                if (item.IsDeleted !== "1") {
                  return (
                    <tr key={item.Id} id={item.UserName}>
                      <td style={{height:"100px"}}>
                        <img
                        key={4}
                        src={item.Picture}
                        alt="nothing"
                        width="130px"
                        height="110px">
                        </img>
                      </td>
                      <td key={1}>{item.UserName}</td>
                      <td key={2}>{item.Phone}</td>
                      <td key={3}>{item.Email}</td>
                      <td key={5}>{this.age(item.BirthDate)}</td>
                      <td key={6}>{this.entryDate(item.EntryDate)}</td>
                      <td>
                        <button 
                          className="noselect"
                          style={{textAlign: "center", width:"100%", marginTop: "2px"}}
                          key={item.UserName}
                          onClick={this.edit}
                          value={item.Id}
                          id={item.UserName}
                        >
                          Edit
                        </button>
                        <button 
                          className="noselect"
                          style={{textAlign: "center", width:"100%", marginTop: "2px"}}
                          key={item.UserName}
                          onClick={this.hide}
                          value={item.Id}
                        >
                          Delete
                        </button>
                       </td>
                    </tr>
                  );
                } else {
                  return <div style={{ display: "none" }}></div>;
                }
              })}
            </tbody>
          </table>
    </div>
  );
}
}

export default Table;
