import React, { Component } from 'react'
import { withRouter } from "react-router-dom";

 class Pages extends Component {
    constructor(props) {
        super(props);
        this.state = {
            url: "https://localhost/phprest/paltimapi.php",
            image: "../images/de.jpg",
            appcode:"",
            api: "",
            src: "https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/appartmentPlans/"
        }
        this.callApi = this.callApi.bind(this);
    }
    componentDidMount() {
        this.callApi()
    }
    callApi() {
        let id = this.props.match.params.id;
        id = id.slice(1,id.length)
        this.setState({appcode: id.slice(1,id.length)});
        fetch(this.state.url, {
            method: "POST",
            body: JSON.stringify({
              type: "id",
              id: id,
            }),
            headers: {
              "Content-type": "application/json; charset=UTF-8",
            },
          })
            .then((res) => res.json())
            .then((res) => this.setState({ api: res }));
      }
    render() {
        return (
            <div>
                <img src="https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/appartmentPlans/1A3.12.png"  width="50%" height="500px"/>
                <div>{this.state.id}</div>
            </div>
        )
    }
}
export default withRouter(Pages)
