import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import i18next from "i18next";
import { withTranslation } from "react-i18next";
import magnify from 'react-magnifying-glass'

class Pages extends Component {
  constructor(props) {
    super(props);
    this.state = {
      url: "https://localhost/phprest/paltimapi.php",
      image: "../images/de.jpg",
      appcode: "",
      api: "d",
      src: "https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/apartmentImagePlans/",
    };
    this.callApi = this.callApi.bind(this);
  }
  componentDidMount() {
    this.callApi();
  }
  callApi() {
    let id = this.props.match.params.id;
    id = id.slice(1, id.length);
    this.setState({ appcode: id.slice(0, id.length) });
    fetch(this.state.url, {
      method: "POST",
      body: JSON.stringify({
        type: "id",
        id: id,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    })
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  downloadPlan() {
    window.location.href = this.state.api[0].PDFPLAN;
  }
  changeLanguageRo() {
    this.setState({ language: "ro" }, () => {
      i18next.changeLanguage("ro");
    });
  }
  changeLanguageEn() {
    this.setState({ language: "en" }, () => {
      i18next.changeLanguage("en");
    });
  }
  goBack(){
    window.history.back();
  }
  render() {
    if (this.state.api !== "d" && this.state.api.length !== 0) {
      console.log(this.state.api.length);
      let src = this.state.src + this.state.appcode + ".png";
      const { t } = this.props;
      return (
        <div>
          <div className="container">
            <button onClick={this.changeLanguageEn.bind(this)}>EN</button>
            <button onClick={this.changeLanguageRo.bind(this)}>RO</button>
            <button style={{ marginLeft: "105%", marginTop: "-30px", backgroundColor: "white", marginBottom: "30px", outline: "none"}} onClick={this.goBack.bind(this)}>
            <i
              class="fas fa-times fa-3x"
              
            ></i>
            </button>
            <div className="row">
            <div class="img-magnifier-container">
                <img id="myimage" src={this.state.api[0].IMAGE} width="800px" height="400px" style={{ marginLeft: "-100px", marginTop:"20px" }} />
                <magnify></magnify>
              </div>
              <div className="col-4">
                <table
                  class="table table-striped"
                  style={{ maxWidth: "500px;", marginLeft: "100px", marginTop: "0px"  }}>
                    
                  <thead>
                  <div style={{marginLeft: "-45px", fontSize: "20px"}}>{this.state.api[0].APPCODE}</div>
                    <tr>
                   
                      <th></th>
                      <th style={{width: "40%"}}></th>
                    </tr>
                  </thead>{" "}
                  <tr>
                    <td>{t('specific_building')}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>{" "}
                    <td>
                      <span>{this.state.api[0].BUILDING}</span>
                    </td>
                  </tr>{" "}
                  <tr>
                    <td>{t('floor')}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>{" "}
                    <td>
                      <span>{this.state.api[0].LEVEL}</span> 
                    </td>
                  </tr>{" "}
                  <tr>
                    <td>{t('rooms')}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>{" "}
                    <td>
                      <span>{this.state.api[0].ROOMS}</span>
                    </td>
                  </tr>{" "}
                  <tr>
                    <td>{t('bathrooms')}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td> <td>{this.state.api[0].BATHROOMS}</td>
                  </tr>{" "}
                  <tr>
                  <td>{t('surface')}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>{" "}
                    <td>
                      <span>{this.state.api[0].SURFACE}</span> {t('sqm')}
                    </td>
                  </tr>{" "}
                  <tr>
                    <td>{t('terrace')}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>{" "}
                    <td>
                      <span>{this.state.api[0].TERRACE}</span> {t('sqm')}
                    </td>
                  </tr>
                </table>
               
                <button
                  style={{
                    width: "200px",
                    height: "50px",
                    borderRadius: "10px",
                    marginTop: "20px",
                    marginLeft: "190px"
                  }}
                  onClick={this.downloadPlan.bind(this)}
                >
                  Download Plan
                </button>
              </div>
            </div>
          </div>
        </div>
      );
    } else {
      return <div></div>;
    }
  }
}
export default withTranslation()(withRouter(Pages));
