import React, { Component } from 'react'
import { withRouter } from "react-router-dom";

 class Pages extends Component {
    constructor(props) {
        super(props);
        this.state = {
            url: "https://localhost/phprest/paltimapi.php",
            image: "../images/de.jpg",
            appcode:"",
            api: "",
            src: "https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/appartmentPlans/"
        }
        this.callApi = this.callApi.bind(this);
    }
    componentDidMount() {
        this.callApi()
    }
    callApi() {
        let id = this.props.match.params.id;
        id = id.slice(1,id.length)
        this.setState({appcode: id.slice(0,id.length)});
        fetch(this.state.url, {
            method: "POST",
            body: JSON.stringify({
              type: "id",
              id: id
            }),
            headers: {
              "Content-type": "application/json; charset=UTF-8",
            },
          })
            .then((res) => res.json())
            .then((res) => this.setState({ api: res }));
      }
    render() {
        if (this.state.api !== "") {
        let src =this.state.src + this.state.appcode + ".png";
        return (
            <div>
                <div class="imgbox">
                    <img  src={src} className="center-fit"/>
                
                <div class="card" style={{width: "18rem"}}>
                <div class="card-body">
                    <h5 class="card-title">{this.state.api[0].APPCODE}</h5>
                    <p class="card-text">{this.state.api[0].BUILDING}</p>
                    <p class="card-text">{this.state.api[0].SURFACE}</p>
                    <p class="card-text">{this.state.api[0].LEVEL}</p>
                    <p class="card-text">{this.state.api[0].ROOMS}</p>
                    <p class="card-text">{this.state.api[0].BATHROOMS}</p>
                    <p class="card-text">{this.state.api[0].TERRACE}</p>
                </div>
                </div>
                </div>
            </div>
        )
    } else {
        return (
            <div></div>
        )
    }
}
}
export default withRouter(Pages)
