import React from 'react';
import './App.css';

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      api: ""
    };
    this.callApi = this.callApi.bind(this);
  }

  //Call API
  callApi() {
    fetch(
      "https://localhost/phprest/paltimapi.php",{
        method: 'POST',
	      body: JSON.stringify({
          type: "all"
        }),
	      headers: {
          'Content-type': 'application/json; charset=UTF-8'
	      }}) 
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }))
  }
  componentDidMount() {
    this.callApi();
  }
  render () {
    console.log(this.state.api)
    if(this.state.api !== ""){
  return (
    <div className="container-xl">
      <div className ="row">
        <div className="col">
      <table className="table-sm table-hover" id="table">
            <thead className="table">
              <tr>
                <th scope="col">Building</th>
                <th scope="col">AppCode</th>
                <th scope="col">Rooms</th>
                <th scope="col">Bathrooms</th>
                <th scope="col">Floor</th>
                <th scope="col">Surface</th>
                <th scope="col">Terrace</th>
                <th scope="col">Orientation</th>
                <th scope="col">Status</th>
              </tr>
            </thead>
            <tbody>
              {this.state.api.map((item) => {
                  return (
                    <tr key={item.ID}>
                      <td  key={0} data-label="Building">{item.BUILDING}</td>
                      <td  key={1} data-label="AppCode">{item.APPCODE}</td>
                      <td  key={2} data-label="Rooms">{item.ROOMS}</td>
                      <td  key={2} data-label="Bathrooms">{item.BATHROOMS}</td>
                      <td  key={3} data-label="Lvel">{item.LEVEL}</td>
                      <td  key={4} data-label="Surface">{item.SURFACE}</td>
                      <td  key={5} data-label="Terrace">{item.TERRACE}</td>
                      <td  key={5} data-label="Orientation">{item.ORIENTATION}</td>
                      <td  key={5} data-label="Status">{item.STATUS}</td>
                    </tr>
                  );
              })}
            </tbody>
          </table>
          </div>
        <div className="col">
        <div className="card" style={{width: "28rem", marginLeft: "-25px"}}>
          <div className="card-body">
            <h5 className="card-title">Filters</h5>
            <h6>Number of rooms</h6>
            <div>
           <input type="checkbox"></input>
           <label> 1 Room</label>
           </div>
           <div>
           <input type="checkbox"></input>
           <label> 2 Rooms</label>
           </div>
           <div>
           <input type="checkbox"></input>
           <label> 3 Rooms</label>
          </div>
          <div>
           <input type="checkbox"></input>
           <label> 4 Rooms</label>
          </div>
          <hr></hr>
          <h6>Building</h6>
          <div>
          <input type="checkbox"></input>
           <label> Building C1</label>
           </div>
          <div>
          <input type="checkbox"></input>
           <label> Building C2</label>
           </div>
         <hr></hr>
          </div>
        </div>
        </div>
        </div>
      </div>
  );
  } else {
      return (
        <div>nu</div>
      )
  }
}








}



export default Table;
