import React from "react";
import "./App.css";
import  BootstrapTable  from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import {Modal, Button} from 'react-bootstrap';
import Slider, { Range } from 'rc-slider';
import 'rc-slider/assets/index.css';

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      api: "",
      room1: false,
      room2: false,
      room3: false,
      room4: false,
      building1: false,
      building2: false,
      gfloor: false,
      floor1: false,
      floor2: false,
      floor3: false,
      floor4: false,
      floor5: false,
      floor6: false,
      bathroom1: false,
      bathroom2: false,
      forsale: false,
      sold: false,
      currentPage: 1,
      postsPerPage: 5,
      modalinfo:[],
      showmodal:false,
      show:false,
      sliderValues:[0,100]
    };

    this.callApi = this.callApi.bind(this);

  }
  //Call API
  callApi() {
    fetch(
      "https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/paltimapi.php",
      {
        method: "POST",
        body: JSON.stringify({
          type: "all",
          room1: "da",
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8",
        },
      }
    )
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  componentDidMount() {
    this.callApi();
  }
  handleCheckboxChange(e) {
    this.setState(
      { [e.target.name]: e.target.checked},
      () => {
        this.sendRequest();
      }
    );
  }
  handleSlider(sliderValues){
    this.setState(
      {sliderValues}, 
      () => {
        this.sendRequest();
    })
  }
  async sendRequest() {
    await fetch(
      "https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/paltimapiSort.php",
      {
        method: "POST",
        body: JSON.stringify({
          type: "sort",
          room1: this.state.room1,
          room2: this.state.room2,
          room3: this.state.room3,
          room4: this.state.room4,
          building1: this.state.building1,
          building2: this.state.building2,
          gfloor: this.state.gfloor,
          floor1: this.state.floor1,
          floor2: this.state.floor2,
          floor3: this.state.floor3,
          floor4: this.state.floor4,
          floor5: this.state.floor5,
          floor6: this.state.floor6,
          bathroom1: this.state.bathroom1,
          bathroom2: this.state.bathroom2,
          surface1: this.state.sliderValues[0],
          surface2: this.state.sliderValues[1],
          forsale: this.state.forsale,
          sold: this.state.sold,
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8",
        },
      }
    )
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  paginate(pageNumber) {
    this.setState({ currentPage: pageNumber });
  }

  render() {
    if (this.state.api !== "") {
    console.log(this.state)
      const handleClose = () => {
        this.setState({show: false});
      }
      const handleShow = () => { 
        this.setState({show: true});
      }
      const rowEvents = {
        onClick: (e, row, rowIndex) => {
          if(row.STATUS === "FOR SALE"){
          this.setState({modalinfo: row});
          toggleTrueFalse()
          }
        }
      };
      const toggleTrueFalse = () => {
        handleShow();
      }

      const ModalContent = () =>{
        return (
          <Modal show={this.state.show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>{this.state.modalinfo.APPCODE}</Modal.Title>

            </Modal.Header>
            <Modal.Body>
            <div className="container">
              <div className="row">
                <div className="col">
              <img src="https://picsum.photos/200/300"></img>

              </div>
              <div className="col">
                <hr></hr>
              <h6 className="modalth6">Surface : {this.state.modalinfo.SURFACE}</h6>
              <h6>Building : {this.state.modalinfo.BUILDING}</h6>
              <h6>Floor : {this.state.modalinfo.LEVEL}</h6>
              <h6>Number of rooms : {this.state.modalinfo.ROOMS}</h6>
              <h6>Bathrooms : {this.state.modalinfo.BATHROOMS}</h6>
              <h6 className="modalbh6">Terrace : {this.state.modalinfo.TERRACE}</h6>
              <hr></hr>
              </div>
              </div>
            </div>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>Close</Button>
            </Modal.Footer>
          </Modal>
        )
      }
      const columns = [
        {dataField: "BUILDING", text:"BUILDING"},
        {dataField: "APPCODE", text:"APPCODE"},
        {dataField: "ROOMS", text:"ROOMS"},
        {dataField: "BATHROOMS", text:"BATHROOMS"},
        {dataField: "LEVEL", text:"FLOOR"},
        {dataField: "SURFACE", text:"SURFACE"},
        {dataField: "TERRACE", text:"TERRACE"},
        {dataField: "ORIENTATION", text:"ORIENTATION"},
        {dataField: "STATUS", text:"STATUS"}
      ];
      let room1 = 0,
        room2 = 0,
        room3 = 0,
        room4 = 0,
        buildingc1 = 0,
        buildingc2 = 0,
        groundfloor = 0,
        floor1 = 0,
        floor2 = 0,
        floor3 = 0,
        floor4 = 0,
        floor5 = 0,
        floor6 = 0,
        bathroom1 = 0,
        bathroom2 = 0,
        forsale = 0;
      let pageNumbers = [];
      const indexOfLastPost = this.state.currentPage * this.state.postsPerPage;
      const indexOfFirstPost = indexOfLastPost - this.state.postsPerPage;
      const currentPost = this.state.api.slice(
        indexOfFirstPost,
        indexOfLastPost
      );
      for (
        let i = 1;
        i <= Math.ceil(this.state.api.length / this.state.postsPerPage);
        i++
      ) {
        pageNumbers.push(i);
      }
      for (let i = 0; i < this.state.api.length; ++i) {
        if (this.state.api[i].ROOMS === "1" && this.state.api[i].STATUS === "FOR SALE") {
          ++room1;
        }
        if (this.state.api[i].ROOMS === "2" && this.state.api[i].STATUS === "FOR SALE") {
          ++room2;
        }
        if (this.state.api[i].ROOMS === "3"  && this.state.api[i].STATUS === "FOR SALE") {
          ++room3;
        }
        if (this.state.api[i].ROOMS === "4"  && this.state.api[i].STATUS === "FOR SALE") {
          ++room4;
        }
        if (this.state.api[i].BUILDING === "C1"  && this.state.api[i].STATUS === "FOR SALE") {
          ++buildingc1;
        }
        if (this.state.api[i].BUILDING === "C2"  && this.state.api[i].STATUS === "FOR SALE") {
          ++buildingc2;
        }
        if (this.state.api[i].LEVEL === "0"  && this.state.api[i].STATUS === "FOR SALE") {
          ++groundfloor;
        }
        if (this.state.api[i].LEVEL === "1"  && this.state.api[i].STATUS === "FOR SALE") {
          ++floor1;
        }
        if (this.state.api[i].LEVEL === "2"  && this.state.api[i].STATUS === "FOR SALE") {
          ++floor2;
        }
        if (this.state.api[i].LEVEL === "3"  && this.state.api[i].STATUS === "FOR SALE") {
          ++floor3;
        }
        if (this.state.api[i].LEVEL === "4"  && this.state.api[i].STATUS === "FOR SALE") {
          ++floor4; 
        }
        if (this.state.api[i].LEVEL === "5"  && this.state.api[i].STATUS === "FOR SALE") {
          ++floor5;
        }
        if (this.state.api[i].LEVEL === "6"  && this.state.api[i].STATUS === "FOR SALE") {
          ++floor6;
        }
        if (this.state.api[i].BATHROOMS === "1"  && this.state.api[i].STATUS === "FOR SALE") {
          ++bathroom1;
        }
        if (this.state.api[i].BATHROOMS === "2"  && this.state.api[i].STATUS === "FOR SALE") {
          ++bathroom2;
        }
        if (this.state.api[i].STATUS === "FOR SALE") {
          ++forsale;
        }
      }
      return (
        <div className="container-fluid">
          <div className="row">
            <div className="col">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Filters</h5>
                  <h6>Number of rooms</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="room1"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 1 Room ---- Available : {room1}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="room2"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 2 Rooms ---- Available : {room2}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="room3"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 3 Rooms ---- Available : {room3}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="room4"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 4 Rooms ---- Available : {room4}</label>
                  </div>
                  <hr></hr>
                  <h6>Building</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="building1"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Building C1 ---- Available : {buildingc1}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="building2"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Building C2 ---- Available : {buildingc2}</label>
                  </div>
                  <hr></hr>
                  <div className="container">
                    <h6 style={{ marginLeft: "-15px" }}>Floor Level</h6>
                    <div className="row" style={{ marginLeft: "-30px" }}>
                      <div className="col">
                        <div>
                          <input
                            type="checkbox"
                            name="gfloor"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> G Floor ---- Available : {groundfloor}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor1"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 1 ---- Available : {floor1}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor2"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 2 ---- Available : {floor2}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor3"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 3 ---- Available : {floor3}</label>
                        </div>
                      </div>
                      <div className="col">
                        <div>
                          <input
                            type="checkbox"
                            name="floor4"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 4 ---- Available : {floor4}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor5"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 5 ---- Available : {floor5}</label>
                        </div>
                        <div>
                          <input
                            type="checkbox"
                            name="floor6"
                            onChange={this.handleCheckboxChange.bind(this)}
                          ></input>
                          <label> Floor 6 ---- Available : {floor6}</label>
                        </div>
                      </div>
                    </div>
                  </div>
                  <hr></hr>
                  <h6>Bathrooms</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="bathroom1"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 1 Bathroom ---- Available : {bathroom1}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="bathroom2"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> 2 Bathroom ---- Available : {bathroom2}</label>
                  </div>
                  <hr></hr>
                  <p>{this.state.sliderValues[0]}-{this.state.sliderValues[1]}</p>
                  <Range
                  min={0}
                  max={100}
                  onChange={this.handleSlider.bind(this)}
                  defaultValue={this.state.sliderValues}></Range>
                  <hr></hr>
                  <h6>Status</h6>
                  <div>
                    <input
                      type="checkbox"
                      name="forsale"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> For Sale ---- Available : {forsale}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="sold"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Sold</label>
                  </div>
                </div>

              </div>


            </div>
            <div className="col">
            <BootstrapTable className="da"
            keyField="ID"
            data={this.state.api}
            columns={columns}
            pagination={paginationFactory()}
            rowEvents={rowEvents}
            />
            {this.state.show ? <ModalContent/> : null}

            </div>
          </div>



        </div>
      );
    } else {
      return <div>Api not started</div>;
    }
  }
}

export default Table;
