import React from "react";
import "./App.css";
import BootstrapTable from "react-bootstrap-table-next";
import { Modal, Button } from "react-bootstrap";
import { Range } from "rc-slider";
import "rc-slider/assets/index.css";

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      url: "https://marianadascalitei.dev.ascensys.ro/paltimsalestool/api/paltimapi.php",
      api: "",
      room1: false,
      room2: false,
      room3: false,
      room4: false,
      building1: false,
      building2: false,
      floor0: false,
      floor1: false,
      floor2: false,
      floor3: false,
      floor4: false,
      floor5: false,
      floor6: false,
      bathroom1: false,
      bathroom2: false,
      forsale: false,
      sold: false,
      currentPage: 1,
      modalinfo: [],
      showmodal: false,
      show: false,
      sliderValues: [0, 100],
      pageNumber: 1,
      postsPerPage:10,
      totalposts:123,
    };

    this.callApi = this.callApi.bind(this);
  }
  //Call API
  callApi() {
    fetch(this.state.url, {
      method: "POST",
      body: JSON.stringify({
        type: "all",
        room1: "da",
        postsPerPage:this.state.postsPerPage,
        pageNumber:this.state.pageNumber
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    })
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  componentDidMount() {
    this.callApi();
  }
  handleCheckboxChange(e) {
    this.setState({ [e.target.name]: e.target.checked }, () => {
      this.sendRequest();
    });
  }
  handleSlider(sliderValues) {
    this.setState({ sliderValues }, () => {
      this.sendRequest();
    });
  }
  async sendRequest() {
    await fetch(this.state.url, {
      method: "POST",
      body: JSON.stringify({
        type: "sort",
        room1: this.state.room1,
        room2: this.state.room2,
        room3: this.state.room3,
        room4: this.state.room4,
        building1: this.state.building1,
        building2: this.state.building2,
        floor0: this.state.floor0,
        floor1: this.state.floor1,
        floor2: this.state.floor2,
        floor3: this.state.floor3,
        floor4: this.state.floor4,
        floor5: this.state.floor5,
        floor6: this.state.floor6,
        bathroom1: this.state.bathroom1,
        bathroom2: this.state.bathroom2,
        surface1: this.state.sliderValues[0],
        surface2: this.state.sliderValues[1],
        forsale: this.state.forsale,
        sold: this.state.sold,
        pageNumber: this.state.pageNumber,
        postsPerPage: this.state.postsPerPage
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    })
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }));
  }
  changePage(e) {
    this.setState({ pageNumber: e.target.innerHTML}, () => {
      this.sendRequest();
    });
  }
  display(){
   let div =  document.getElementById("content");
   if (div.style.maxHeight){
    div.style.maxHeight = null;
  } else {
    div.style.maxHeight = div.scrollHeight + "px";
  }
  }
  resetFilters(){
   document.getElementById("checkboxroom1").checked = false;
   document.getElementById("checkboxroom2").checked = false;
   document.getElementById("checkboxroom3").checked = false;
   document.getElementById("checkboxroom4").checked = false;
   document.getElementById("checkboxfloorL1").checked = false;
   document.getElementById("checkboxfloorL2").checked = false;
   document.getElementById("checkboxfloorL3").checked = false;
   document.getElementById("checkboxfloorL4").checked = false;
   document.getElementById("checkboxfloorL5").checked = false;
   document.getElementById("checkboxfloorL6").checked = false;
   document.getElementById("checkboxfloorL7").checked = false;
   document.getElementById("checkboxbathroom1").checked = false;
   document.getElementById("checkboxbathroom2").checked = false;
   document.getElementById("checkboxforsale").checked = false;

   }

  render() {
    if (this.state.api !== "") {
      console.log("adi")
      let pageNumbers = [];
      for (let i = 1;  i <= this.state.api[0].numberOfItems / this.state.postsPerPage + 1 ;i++) {
          pageNumbers.push(i);
      }

      const handleClose = () => {
        this.setState({ show: false });
      };
      const handleShow = () => {
        this.setState({ show: true });
      };
      const rowEvents = {
        onClick: (e, row, rowIndex) => {
          if (row.STATUS === "FOR SALE") {
            this.setState({ modalinfo: row });
            toggleTrueFalse();
          }
        },
      };
      const toggleTrueFalse = () => {
        handleShow();
      };

      const ModalContent = () => {
        return (
          <Modal show={this.state.show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>{this.state.modalinfo.APPCODE}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <div className="container">
                <div className="row">
                  <div className="col">
                    <img
                      src="https://picsum.photos/200/300"
                      alt="nothing"
                    ></img>
                  </div>
                  <div className="col">
                    <hr></hr>
                    <h6 className="modalth6">
                      Surface : {this.state.modalinfo.SURFACE}
                    </h6>
                    <h6>Building : {this.state.modalinfo.BUILDING}</h6>
                    <h6>Floor : {this.state.modalinfo.LEVEL}</h6>
                    <h6>Number of rooms : {this.state.modalinfo.ROOMS}</h6>
                    <h6>Bathrooms : {this.state.modalinfo.BATHROOMS}</h6>
                    <h6 className="modalbh6">
                      Terrace : {this.state.modalinfo.TERRACE}
                    </h6>
                    <hr></hr>
                  </div>
                </div>
              </div>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Close
              </Button>
            </Modal.Footer>
          </Modal>
        );
      };
      const columns = [
        { dataField: "BUILDING", text: "BUILDING" },
        { dataField: "APPCODE", text: "APPCODE" },
        { dataField: "ROOMS", text: "ROOMS" },
        { dataField: "BATHROOMS", text: "BATHROOMS" },
        { dataField: "LEVEL", text: "FLOOR" },
        { dataField: "SURFACE", text: "SURFACE(SQM)" },
        { dataField: "TERRACE", text: "TERRACE(SQM)" },
        { dataField: "ORIENTATION", text: "ORIENTATION" },
        { dataField: "STATUS", text: "STATUS" },
      ];
      let bathrooms = [null, 0, 0];
      let bathroomsnumbers = ["1", "2"];
      let forsale = 0;
      let floors = [0, 0, 0, 0, 0, 0, 0];
      let floornumbers = ["L1", "L2", "L3", "L4", "L5", "L6", "L7"];
      let buildings = [0, 0];
      let rooms = [null, 0, 0, 0, 0];
      let roomnumbers = ["1", "2", "3", "4"];

      return (
        <div className="container-fluid">
          <div className="row">
            <div className="col">
              <button type="button" class="collapsible" onClick={this.display.bind(this)}>Filters</button>
              <div className="content" id="content">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Filters</h5>
                  <h6 style={{marginLeft: "5px"}}> <i class="fas fa-bed"> </i> Number of rooms</h6>
                  {roomnumbers.map((room) => {
                    return (
                      <div key={room}>
                        <input
                          type="checkbox"
                          id={"checkboxroom" + room}
                          name={"room" + room}
                          onChange={this.handleCheckboxChange.bind(this)}
                        >
                        </input>

                        {room == 1 ? <label style={{display: "inline"}}>Room &gt; Available : </label> : <label>Rooms &gt; Available : </label>}
                        {room == 1 ? <div style={{display: "inline"}}>{" "}{this.state.api[room].countroom1}</div> : null}
                        {room == 2 ? <div style={{display: "inline"}}>{" "}{this.state.api[room].countroom2}</div> : null}
                        {room == 3 ? <div style={{display: "inline"}}>{" "}{this.state.api[room].countroom3}</div> : null}
                        {room == 4 ? <div style={{display: "inline"}}>{" "}{this.state.api[room].countroom4}</div> : null}
                      </div>
                    );
                  })}
                  <hr></hr>
                  <h6 style={{marginLeft: "5px"}}> <i class="far fa-building"></i> Buildings</h6>
                  <div>
                    <input
                      type="checkbox"
                      id="building1"
                      name="building1"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Building C1 &gt; Available : </label>
                    <div style={{display: "inline"}}>{" "}{this.state.api[0].countbuilding1}</div>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="building2"
                      disabled = "disabled"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Building C2 &gt; Coming Soon</label>
                  </div>
                  <hr></hr>
                  <div className="container">
                    <h6 style={{ marginLeft: "-10px" }}><i class="fas fa-layer-group"></i> Floor Level</h6>
                    <div className="" style={{ marginLeft: "-15px" }}>
                      {floornumbers.map((floor) => {
                        return (
                          <div key={floor}>
                            <input
                              type="checkbox"
                              id={"checkboxfloor" + floor}
                              name={"floor" + floor}
                              onChange={this.handleCheckboxChange.bind(this)}
                            ></input>
                            <label>
                              {" "}
                              Floor {floor} &gt; Available : 
                            </label>
                            {floor == "L1" ? <div style={{display: "inline"}}>{" "}{this.state.api[0].countfloor1}</div> : null}
                            {floor == "L2" ? <div style={{display: "inline"}}>{" "}{this.state.api[0].countfloor2}</div> : null}
                            {floor == "L3" ? <div style={{display: "inline"}}>{" "}{this.state.api[0].countfloor3}</div> : null}
                            {floor == "L4" ? <div style={{display: "inline"}}>{" "}{this.state.api[0].countfloor4}</div> : null}
                            {floor == "L5" ? <div style={{display: "inline"}}>{" "}{this.state.api[0].countfloor5}</div> : null}
                            {floor == "L6" ? <div style={{display: "inline"}}>{" "}{this.state.api[0].countfloor6}</div> : null}
                            {floor == "L7" ? <div style={{display: "inline"}}>{" "}{this.state.api[0].countfloor7}</div> : null}
                          </div>
                        );
                      })}
                    </div>
                  </div>
                  <hr></hr>
                  <h6 style={{ marginLeft: "5px" }}> <i class="fas fa-restroom"></i> Bathrooms</h6>
                  {bathroomsnumbers.map((bathroom) => {
                    return (
                      <div key={bathroom}>
                        <input
                          type="checkbox"
                          id={"checkboxbathroom"  + bathroom}
                          name={"bathroom" + bathroom}
                          onChange={this.handleCheckboxChange.bind(this)}
                        ></input>
                        <label>
                          {" "}
                          Bathroom {bathroom} &gt; Available :{" "}

                        </label>
                        {bathroom == 1 ? <div style={{display: "inline"}}>{" "}{this.state.api[0].countbathroom1}</div> : null}
                        {bathroom == 2 ? <div style={{display: "inline"}}>{" "}{this.state.api[0].countbathroom2}</div> : null}
                      </div>
                    );
                  })}
                  <hr></hr>
                  <h6 style={{ marginLeft: "5px"}}> <i class="fas fa-ruler-combined"></i> Surface</h6>
                  <p style={{ marginLeft: "5px",fontWeight: "1000"}}>
                 {this.state.sliderValues[0]}sqm - {this.state.sliderValues[1]}sqm
                  </p>
                  <Range
                    min={0}
                    max={100}
                    onChange={this.handleSlider.bind(this)}
                    defaultValue={this.state.sliderValues}
                  ></Range>
                  <hr></hr>
                  <h6 style={{ marginLeft: "5px" }}> <i class="fas fa-lock-open"></i> Status</h6>
                  <div>
                    <input
                      type="checkbox"
                      id ="checkboxforsale"
                      name="forsale"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> For Sale &gt; Available : {forsale}</label>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      name="sold"
                      disabled = "disabled"
                      onChange={this.handleCheckboxChange.bind(this)}
                    ></input>
                    <label> Sold</label>
                  </div>
                </div>
                <button onClick={this.resetFilters.bind(this)}>Reset filters</button>
              </div>

              </div>

            </div>
            <div className="col-9">
              <BootstrapTable
                className="table-borderless"
                keyField="ID"
                data={this.state.api}
                columns={columns}
                bordered={false}


                rowEvents={rowEvents}
              />
              {this.state.show ? <ModalContent /> : null}
              <nav aria-label="...">
                <ul className="pagination pagination-lg">
                  {pageNumbers.map((item) => {
                    return (
                    <li key={item} value={item} className="page-item" onClick={this.changePage.bind(this)}><a className="page-link" href="#">{item}</a></li>
                    )
                  })}
                </ul>
                <select>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value="30">30</option>
                <option value="40">40</option>
                </select>
              </nav>
            </div>

          </div>

        </div>
      );
    } else {
      return <div>Fetching data</div>;
    }
  }
}

export default Table;
