import React from 'react';
import './App.css';

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      api: ""
    };
    this.callApi = this.callApi.bind(this);
  }
  callApi() {
    fetch(
      "https://localhost/phprest/paltimapi.php",{
        method: 'POST',
	      body: JSON.stringify({
          type: "all"
        }),
	      headers: {
          'Content-type': 'application/json; charset=UTF-8'
	      }}) 
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }))
  }
  componentDidMount() {
    this.callApi();
  }
  render () {
    console.log(this.state.api)
    if(this.state.api !== ""){
  return (
    <div>
      <table className="table table-hover" id="table">
        <caption>Statement Summary</caption>
            <thead className="table">
              <tr>
                <th scope="col">Building</th>
                <th scope="col">AppCode</th>
                <th scope="col">Rooms</th>
                <th scope="col">Bathrooms</th>
                <th scope="col">level</th>
                <th scope="col">Surface</th>
                <th scope="col">Terrace</th>
                <th scope="col">Orientation</th>
                <th scope="col">Status</th>
              </tr>
            </thead>
            <tbody>
              {this.state.api.map((item) => {
                  return (
                    <tr key={item.ID}>
                      <td  key={0}>{item.BUILDING}</td>
                      <td  key={1}>{item.APPCODE}</td>
                      <td  key={2}>{item.ROOMS}</td>
                      <td  key={2}>{item.BATHROOMS}</td>
                      <td key={3}>{item.LEVEL}</td>
                      <td  key={4}>{item.SURFACE}</td>
                      <td  key={5}>{item.TERRACE}</td>
                      <td  key={5}>{item.ORIENTATION}</td>
                      <td  key={5}>{item.STATUS}</td>
                    </tr>
                  );
              })}
            </tbody>
          </table>
    </div>
  );
            } else {
              return (
                <div>nu</div>
              )
            }
}








}



export default Table;
