import React from 'react';
import './App.css';
import axios from 'axios'

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      api: "",
      room1: false,
      room2: false,
      room3: false,
      room4: false,
      building1: false,
      building2: false,
      gfloor: false,
      floor1:false,
      floor2: false,
      floor3: false,
      floor4: false,
      floor5: false,
      floor6: false,
      bathroom1: false,
      bathroom2: false,
      surface: 0,
      forsale: false,
      sold:false,
    };

    this.callApi = this.callApi.bind(this);
  }

  //Call API
  callApi() {
    fetch(
      "http://localhost/phprest/paltimapi.php",{
        method: 'POST',
	      body: JSON.stringify({
          type: "all"
        }),
	      headers: {
          'Content-type': 'application/json; charset=UTF-8'
	      }}) 
      .then((res) => res.json())
      .then((res) => this.setState({ api: res }))
  }
  componentDidMount() {
    this.callApi();
  }
  handleCheckboxChange(e) {
    this.setState({[e.target.name]: e.target.checked}, () => {
    this.sendRequest();
    })
  }
  handleSlider(e){
    this.setState({surface: e.target.value}, () => {
      this.sendRequest();
    })
  }
  async sendRequest(){
    const data = new FormData();
    data.append('type',"sort");
    data.append('room1',this.state.room1);
    data.append('room2',this.state.room2);
    data.append('room3',this.state.room3);
    data.append('room4',this.state.room4);
    data.append('building1',this.state.building1);
    data.append('building2',this.state.building2);
    data.append('gfloor',this.state.gfloor);
    data.append('foor1',this.state.floor1);
    data.append('foor2',this.state.floor2);
    data.append('foor3',this.state.floor3);
    data.append('foor4',this.state.floor4);
    data.append('foor5',this.state.floor5);
    data.append('foor6',this.state.floor6);
    data.append('bathroom1',this.state.bathroom1);
    data.append('bathroom2',this.state.bathroom2);
    data.append('surface',this.state.surface);
    data.append('forsale',this.state.forsale);
    data.append('sold',this.state.sold);

    let resp = await axios.post(
      "http://localhost/phprest/paltimapiSort.php",
      data,
      {
        headers: {
          "content-type": "multipart/form-data",
        },
      }
    ).then((resp) => resp.json())
  }
  render () {
    console.log(this.state)
    if(this.state.api !== ""){

  return (
    <div className="container-xl">
      <div className ="row">
        <div className="col">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Filters</h5>
            <h6>Number of rooms</h6>
            <div>
           <input type="checkbox" name="room1" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> 1 Room</label>
           </div>
           <div>
           <input type="checkbox" name="room2" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> 2 Rooms</label>
           </div>
           <div>
           <input type="checkbox" name="room3" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> 3 Rooms</label>
          </div>
          <div>
           <input type="checkbox" name="room4" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> 4 Rooms</label>
          </div>
          <hr></hr>
          <h6>Building</h6>
          <div>
          <input type="checkbox" name="building1" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Building C1</label>
           </div>
          <div>
          <input type="checkbox" name="building2" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Building C2</label>
           </div>
         <hr></hr>
         <div className="container">
           <h6 style={{marginLeft: "-15px"}}>Floor Level</h6>
           <div className="row" style={{marginLeft: "-30px"}}>
             <div className="col">
             <div>
          <input type="checkbox" name="gfloor" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Ground Floor</label>
           </div>
         <div>
          <input type="checkbox" name="floor1" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Floor 1</label>
           </div>
           <div>
          <input type="checkbox" name="floor2" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Floor 2</label>
           </div>
           <div>
          <input type="checkbox" name="floor3" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Floor 3</label>
           </div>
           </div>
           <div className="col">
           <div>
          <input type="checkbox" name="floor4" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Floor 4</label>
           </div>
           <div>
          <input type="checkbox" name="floor5" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Floor 5</label>
           </div>
           <div>
          <input type="checkbox" name="floor6" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Floor 6</label>
           </div>
           </div>
          </div>
           </div>
           <hr></hr>
           <h6>Bathrooms</h6>
           <div>
          <input type="checkbox" name="bathroom1" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> 1 Bathroom</label>
           </div>
           <div>
          <input type="checkbox" name="bathroom2" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> 2 Bathroom</label>
           </div>
           <hr></hr>
           <div className="slidecontainer">
             <h6> Surface :</h6>
            <input type="range" min="1" max="100"  className="slider" name="surface" onChange={this.handleSlider.bind(this)} ></input>
            </div>
            <hr></hr>
            <h6>Status</h6>
            <div>
          <input type="checkbox" name="forsale" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> For Sale</label>
           </div>
           <div>
          <input type="checkbox" name="sold" onChange={this.handleCheckboxChange.bind(this)}></input>
           <label> Sold</label>
           </div>
           </div>
        </div>
        </div>
        <div className="col">
      <table className="table-sm table-hover" id="table">
            <thead className="table">
              <tr>
                <th scope="col">Building</th>
                <th scope="col">AppCode</th>
                <th scope="col">Rooms</th>
                <th scope="col">Bathrooms</th>
                <th scope="col">Floor</th>
                <th scope="col">Surface</th>
                <th scope="col">Terrace</th>
                <th scope="col">Orientation</th>
                <th scope="col">Status</th>
              </tr>
            </thead>
            <tbody>
              {this.state.api.map((item) => {
                  return (
                    <tr key={item.ID}>
                      <td  key={0} data-label="Building">{item.BUILDING}</td>
                      <td  key={1} data-label="AppCode">{item.APPCODE}</td>
                      <td  key={2} data-label="Rooms">{item.ROOMS}</td>
                      <td  key={3} data-label="Bathrooms">{item.BATHROOMS}</td>
                      <td  key={4} data-label="Level">{item.LEVEL}</td>
                      <td  key={5} data-label="Surface">{item.SURFACE}</td>
                      <td  key={6} data-label="Terrace">{item.TERRACE}</td>
                      <td  key={7} data-label="Orientation">{item.ORIENTATION}</td>
                      <td  key={8} data-label="Status">{item.STATUS}</td>
                    </tr>
                  );
              })}
            </tbody>
          </table>
          </div>
          </div>
      </div>
  )
  } else {
      return (
        <div>nu</div>
      )
  }
}
}

export default Table;
